//Function that generates a random temperature based on a normal distribution with a specified mean (average) and standard deviation
function getRandTemp(mean, stdDev, min, max) {
    //Box-Muller algorithm for generating random numbers with normal distribution
    var u1 = Math.random();         //Generates a random number between 0 and 1 (excluding 1)
    var u2 = Math.random();         //Generates a random number between 0 and 1 (excluding 1)
    var z = Math.sqrt(-2 * Math.log(u1)) * Math.cos(2 * Math.PI * u2);

    //Scale the generated value to fit the desired standard deviation and mean
    var temp = z * stdDev + mean;

    //Make sure the temperature remains between the desired minimum and maximum
    temp = Math.min(max, Math.max(min, temp));

    //Round the number to two decimal places
    var roundedTemp = temp.toFixed(2);

    return roundedTemp;
}

