//Function that generates a random humidity value considering the temperature
function getHumidity(temperature) {

    //Define the correlation between temperature and humidity
    var baseHumidity = 50;                      //Base humidity
    var humidityIncreasePerDegree = 0.2;        //Humidity increase per degree Celsius

    //Convert temperature to a number (in case it's a string)
    temperature = parseFloat(temperature);

    //Calculate humidity based on temperature
    var humidity = baseHumidity + (temperature * humidityIncreasePerDegree);

    //Introduce a random variation in humidity to make it more realistic
    var randomVariation = Math.random() * 16 - 8;       //Random variation between -8 and +8
    humidity += randomVariation;

    //Ensure humidity remains between 0 and 100 %
    humidity = Math.max(0, Math.min(100, humidity));

    //Round humidity to two decimal places
    humidity = parseFloat(humidity.toFixed(2));

    return humidity.toString();         //Convert the number into a string
}

