function calculateTime(kilometersRemaining, averageSpeed) {
    var hoursRemaining = Math.floor(kilometersRemaining / averageSpeed);
    var minutesRemaining = Math.round((kilometersRemaining / averageSpeed - hoursRemaining) * 60);

    //Check if minutes exceed 60 and convert part of the minutes to hours
    if (minutesRemaining >= 60) {
        var additionalHours = Math.floor(minutesRemaining / 60);
        hoursRemaining += additionalHours;
        minutesRemaining -= additionalHours * 60;
    }

    return hoursRemaining + " hours and " + minutesRemaining + " minutes";      //Return the remaining time as hours and minutes
}

