/*
The event_index variable can be global because we consume same-cluster tuples in bulk fashion, i.e.
we consume all the cluster's tuples before consuming another cluster...
*/
var event_index = 0;

// Iterate on the event
for (let raw_row of entries){

	// split the event by ; (to obtaine the different fileds)
	let row = raw_row.split(';')

	// Get the cluster_id, and check if it is already in the dict
	if (row[0] in clusters){ // if yes

		/* calculate the delay for this event 
		First: we divide the total time occupied by the cluster by the number of events
		in the cluster, to get the cluster interarrival time.

		Second: we multiply such interarrival time times the current event index to compute 
		the time offset of this very event. This is the tuple-specific delay that nodeRED is
		later going to enforce to our generative module.
		*/
		event_index = event_index + 1
		row[6] = (parseFloat(row[6])/parseFloat(row[5])) * event_index

		// get the clusters events, and add the new event to the cluster
		events = clusters[row[0]]
		events.push(row)
		clusters[row[0]] = events

	} else{ // if no

		// Create a new entry in the dict
		events = []
		event_index = 0
		row[6] = 0
		events.push(row)
		clusters[row[0]] = events
	}

}

